import React from 'react';
import { Link, ReactDOM, findDOMNode } from 'react-router-dom'
import RouteSwitch from '../RouteSwitch';

const Nav = ({shopCart}) => {
    const shopCartEle = document.getElementById("shopCart")
    const shopEle = ReactDOM.findDOMNode(<RouteSwitch/>).getElementsByClassName("shop")
    function shopCartDisplayOnOff() {
        shopCartEle.style.display === 'none' ? shopCartEle.style.display = 'flex' : shopCartEle.style.display = 'none'
        shopEle.style.opacity = "70%"
    } 
    return(
        <nav>
            <h3>nav</h3>
             <ul className="nav-links">
                <Link to='/'>
                    <li>Home</li>    
                </Link>
                <Link to='/deals'>
                    <li>Deals</li> 
                </Link>
                <Link to='/about'>
                    <li>About</li>    
                </Link>

                <Link >
                    <li className='cartNav' onClick={() => shopCartDisplayOnOff()}>{shopCart.length} Cart</li>    
                </Link>

             </ul>
        </nav>
    )
}

export default Nav
